import { Logger } from "./logger";
export class LoggerModule {
    constructor(level, module) {
        this.level = level;
        this.module = module;
    }
    init(coreInjector) {
        const logger = coreInjector.get(Logger);
        logger.config(this.level, this.module);
        return Promise.resolve(this);
    }
    getConfig() {
        return {
            logger: {
                level: this.level,
                module: this.module,
            }
        };
    }
    terminate() {
        return Promise.resolve(this);
    }
}
